!function(){"use strict";$.getJSON(window.apiUrl+"/activity/deals/",function(t){var i=$(".template").children().first().clone();$.each(t,function(t,e){var n=i.clone();n.find(".photo img").attr("src","images/"+e.photo),n.find(".title").text(e.title),n.find(".restaurant .name").text(e.restaurant).wrap('<a href="restaurant.html?id='+e.restaurant_id+'"></a>'),n.find(".details").text(e.details),n.find(".more").text(e.more),$(".main-div").append(n)})}).done(function(){$(".more-button").click(function(){$(this).fadeOut(),$(this).parent().find(".more").slideDown();var t=$(this).parent().parent();t.find(".photo").animate({height:t.find(".photo img").height()},500)})})}();